    class RandomSender
    {
        private readonly Context context;
        private Random random;
        private Timer timer;

        public RandomSender(Context context)
        {
            this.context = context;
            this.random = new Random();
            this.timer = new(5000);
            this.timer.Elapsed += Timer_Elapsed;
            this.timer.AutoReset = true;
            this.timer.Enabled = true;
        }

        private void Timer_Elapsed(object sender, ElapsedEventArgs e)
        {
            var items = Data.Areapeers.Concat(Data.AreapeersForEEW).Concat(Data.Earthquakes).Concat(Data.Tsunamis).ToArray();
            var packet = Packet.Parse(items[random.Next(0, items.Count())]);
            packet.Data = packet.Data.Skip(2).ToArray();
            context.SendAll(PacketSigner.Sign(packet));
        }
    }
